import { GET_VIEWER_ERROR, GET_VIEWER_PROCESS, GET_VIEWER_SUCCESS } from "../action/viewaction";


const initialState = {
    product: [],
    isLoading: false,
    isError: null,
  };


  const viewer_reducer = (state = initialState, action) => {
      console.log(action, "Data from reducer");
      const data = action.data
    switch (action.type) {
        case GET_VIEWER_PROCESS:{
            return{
                ...state,
                isLoading:true,
            }
        }
        case GET_VIEWER_SUCCESS:{
            return{
                data,
                ...state,
                isError:false,
                isLoading:false,
            }
        }
        case GET_VIEWER_ERROR:{
            return{
                ...state,
                isError:true,
                isLoading:false,
                data
            }
        }
        default:
            return({
                name:"Hello"
            })
            break;
    }
}

export default viewer_reducer
