import { call, put } from "redux-saga/effects";
import { get_viewer_api } from "../../viewer/api/viewerapi";
import { GET_VIEWER_ERROR, GET_VIEWER_SUCCESS } from "../../viewer/action/viewaction";


export function* get_viewer_handler (){
    try {
        const res = yield call(get_viewer_api)
        const status = res.status
        const data = res.data
        if(status === 200 || status === 201){
            yield put({type:GET_VIEWER_SUCCESS,data})
        }
        else{
            yield put({type:GET_VIEWER_ERROR,data})
        }
    } catch (error) {
        console.log(error);
    }
}
